import { View, Text, Dimensions } from 'react-native';
import React from 'react';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';
import CartScreen from './CartScreen.jsx';
import OfferScreen from './OfferScreen.jsx';
import AccountScreen from './AccountScreen.jsx';
import { Ionicons } from '@expo/vector-icons';
import HomeScreen from './HomeScreen.jsx';

const windowHeight = Dimensions.get('screen').height;


const Tab = createBottomTabNavigator();

const BottomTabNavigator = () => {
  return (
    <Tab.Navigator
     screenOptions={({route}) => ({
        tabBarIcon: ({ focused, color, size }) => {
            let iconName;
            if(route.name === 'Home'){
                iconName = focused ? 'home' : 'home-outline';
            } else if(route.name === 'Offer'){
                iconName = focused ? 'flower' : 'flower-outline';
            } else if(route.name === 'Cart'){
                iconName = focused ? 'basket' : 'basket-outline';
            }else if(route.name === 'Account'){
                iconName = focused ? 'person' : 'person-outline';
            }
            return <Ionicons name={iconName} color={color} size={size} />
        },
        tabBarActiveTintColor: 'tomato',
        tabBarInactiveTintColor: 'white',
        headerShown: false,
        tabBarStyle: {backgroundColor: '#16213E'}
     })
    }
      >
        <Tab.Screen name="Home" component={HomeScreen} />
        <Tab.Screen name="Offer" component={OfferScreen} />
        <Tab.Screen name="Cart" component={CartScreen} options={{tabBarBadge: 3, tabBarBadgeStyle: {backgroundColor: '#9A1663'}}} />
        <Tab.Screen name="Account" component={AccountScreen} />
     </Tab.Navigator>
  )
}

export default BottomTabNavigator
